}

const styles = StyleSheet.create({
    container: {
        height: '100%',
    },
    innercontainer:{
        margin:20,
        padding:'10%',
        paddingTop:'10%',
        marginBottom:'50%',
        height: '50%',
        justifyContent:'center',
        alignContent:'center',
        backgroundColor:'#e7e7e7',

    }
});
export default AddBoard
