        //     </View>

        //     <View style={styles.innercontainer}>
        //         <Text style={styles.text}>Password :</Text>
        //         <TextInput placeholder='Enter Password' value={loginText} underlineColorAndroid='transparent' style={{borderBottomColor:'black',borderBottomWidth:1,fontSize:20}}/>
        //     </View>

        //     <Button title="Login" onPress={loginFunc} />
        // </View>
    );
}

const styles = StyleSheet.create({
    container:{
        justifyContent:'center',
