import React,{useState} from 'react';
import {Text, View, Button,StyleSheet,Image} from 'react-native';
import { TextInput } from 'react-native-gesture-handler';

import {NavigationContainer} from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';
import HomeScreen from '../screens/HomeScreen';
import LinksScreen from '../screens/LinksScreen';


const Stack = createStackNavigator();
const Login = ({ navigation }) =>{

    const [loginText,setloginText] = ('')
    const [passwordText,setPasswordText] = ('')

    const handleLogin = ()=>{
        //setloginText('hello');
        navigation.navigate('Links');
    }

    const handleTextChange = () =>{
        () =>setloginText({loginText});
        setPasswordText({passwordText});
    }

    const loginFunc =()=>{
        //console.log('hello');

        let url = 'http://127.0.0.1:8000/auth/register/';

        fetch(url, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type':     'application/json',
            },
            body: JSON.stringify({
                loginID: 1
            }),
            credentials: 'same-origin',
        }).then((response) => {
            if (response.status === 403) {
                console.log('Authentication error...');
                
               // this.setState({currentMessage: 'Authentication error.'});
            }
            //console.log(response);
            //handleClick();
            return response.json()
        }).then((responseJson) => {
            
            console.log(responseJson["id"]);
            handleLogin();
            return responseJson;
        }).catch((error) => {
            let message = 'Error saving new bookmark.';
            //this.setState({'currentMessage': message + "\n" + error});
            console.log(error);
        });
    }

    return(
       // handleClick()
        <View style={styles.container}>
        <Image
          style={{width: 50, height: 50}}
          source={{uri: 'https://reactnative.dev/img/tiny_logo.png'}}
        />   
             <View style={styles.innercontainer}>
                <Text style={styles.text}>UserName :</Text>
                <TextInput placeholder='Enter UserName' value={loginText} underlineColorAndroid='transparent' style={{borderBottomColor:'black',borderBottomWidth:1,fontSize:20}}/>
            </View>

            <View style={styles.innercontainer}>
                <Text style={styles.text}>Password :</Text>
                <TextInput placeholder='Enter Password' value={passwordText} 
                underlineColorAndroid='transparent' 
                style={{borderBottomColor:'black',borderBottomWidth:1,fontSize:20}}
                onChangeText={handleTextChange}/>
            </View>

            <Button title="Login" onPress={loginFunc} />
        </View>
    );
}

const styles = StyleSheet.create({
    container:{
        justifyContent:'center',
        alignItems:'center',
    },
    innercontainer:{
        flexDirection:'row',
        justifyContent:'space-evenly',
        margin:10
    },

    text:{
        fontSize:20,
        justifyContent:'center',
        alignContent:'center'
    },
});


export default Login;
